            prefijo = 'luminosity_';
            break;
        case 'presenceSensor':
            prefijo = 'presence_';
            break;

    }
    for (var key2 in newPayloadFields[key]) {
        newMsg.payload_fields[prefijo + key2] =
            newPayloadFields[key][key2];
    }
}
newMsg.metadata = {
    'rxInfo': msg.rxInfo,
    'txInfo': msg.txInfo
};

newMsg.ultimoUplink = "CS";
newMsg.infoDownlink = {"hardware_serial":newMsg.hardware_serial};
return {
    msg: newMsg,
    metadata: metadata,
    msgType: msgType
};

var newObj = msg.payload_fields;
var obj = {};
//Si el obj tiene propiedades que son, a su vez, objetos JSON
//por ejemplo, el caso de un GPS
//tengo que convertirlas a timeseries
//Desafortunadamente, es posible que aquí tengamos que hacer algunas
//conversiones porque ciertos widgets no admiten los datos en el formato
//que los entregan los sensores; por ejemplo, el magnetómetro del RAK7200
//entrega las mediciones en uT, pero el widget brújula requiere directamente un ángulo
//Esto lo podemos resolver con una función de postprocesamiento en el propio alias
//pero el Widget trip animation sólo admite timeseries (no attibutes), por lo que no
//podemos cargar el cercado desde un atributo (o quizás sea mejor tratarlo en la regla del propio dispositivo)
for (var x in newObj) {
    if (typeof(newObj[x]) === 'object') {
        for (var y in newObj[x]) {
            obj[x + '_' + y] = newObj[x][y];
        }
    } else {
        obj[x] = newObj[x];
    }
}
obj.metadata = msg.metadata;
if (!msg.hasOwnProperty('ultimoUplink'))  {
    obj.ultimoUplink = "TTN";
    obj.infoDownlink = {
        "downlink_url": msg.downlink_url,
        "ttn_dev_id": msg.dev_id
    };
}else{
    obj.ultimoUplink =msg.ultimoUplink;
    obj.infoDownlink = msg.infoDownlink;
}

return {
    msg: obj,
    metadata: metadata,
    msgType: msgType
};

var downlinkQueue = JSON.parse(metadata.ss_downlinkQueue);
if (downlinkQueue.length > 0) {
    var downlink = downlinkQueue.shift();
    //La meto en metadata para luego guardarla en los atributos del servidor
    metadata.ss_downlinkQueue=JSON.stringify(downlinkQueue);
    var newMsg = {
        "meta": msg.meta,
        "params": {
            "counter_down": msg.params.counter_down,
            "payload": downlink.payload,
            "port": downlink.port,
            "queue_if_late": true
        },
        "type": "downlink_response"
    };
    return {
        msg: newMsg,
        metadata: metadata,
        msgType: "POST_ATTRIBUTES_REQUEST"
    };
}

return {
    msg: {"downlinkQueue":JSON.parse(metadata.ss_downlinkQueue)},
